// adjust this factor for stronger optical illusion
#define SCALE 60.0
// rotation speed, might be negative to spin counter-clockwise
#define SPEED -25.0

float getColorComponent(float dist, float angle, float thicknessChangeSpeed) {
    return pow(
        (sin(dist * (SCALE) + angle - (iTime * SPEED)) + 1.0) / 2.0,
        (sin(iTime * thicknessChangeSpeed) + 1.0) + 0.2 // we don't want it to get too thick
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 delta = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    float dist = length(delta),
    angle = atan(delta.x, delta.y);
    fragColor = vec4(
        getColorComponent(dist, angle, 1.0),
        getColorComponent(dist, angle, 0.9),
        getColorComponent(dist, angle, 0.7),
        1.0
    );
}
