// Fork of "Tree Study" by wyatt. https://shadertoy.com/view/3dlfWn
// 2020-04-28 22:09:24

Main { Q = B(U); }

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define pi 3.14159265359
#define Main void mainImage(out vec4 Q, vec2 U) 
#define e(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define N 5.
#define I 15.
vec2 hash22(vec2 p) // Dave H
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
float sg (vec2 p, vec2 a, vec2 b) {
    return  length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));

}

void Y (inout vec4 Q, vec2 U, vec4 n) {
	if ((length(U-Q.xy)>length(U-n.xy)||length(Q.xy)<3.)&&
        length(n.xy)>3.) Q = n;
}
Main {
	Q = A(U);
    Q = round(Q);
    if (mod(float(iFrame),N)<.5) {
        if (mod(float(iFrame),N*I)<.5) {
           Q = vec4(R.x*(0.5+0.3*sin(2.61*iTime)),
                    0.15*R.y,
                    R.x*(0.5+0.3*sin(2.61*iTime)),
                    0.);
        } else {
            float
                  n = 1.+mod(floor(float(iFrame)/N/3.),3.),
                  s = mod(floor(U.x+R.x*U.y),n),
                  l = length(Q.zw-Q.xy),
                  a = .1*sin(.1*iTime)+
                	  .3*sin(l+float(iFrame))*
                	  (s-ceil(0.5*n))*pi;
            vec2 h = .1*l*(hash22(floor(Q.xy))*2.-1.);
            vec2 t = Q.xy;
            Q.xy = Q.xy - 0.8*(Q.zw-Q.xy)+h;
            Q.xy = t+(Q.xy-t)*e(a);
        	Q.zw = t;
        }
    }
    else {
        float k = exp2(N-1.-mod(float(iFrame),N));
    	Y(Q,U,A(U+vec2(0,k)));
    	Y(Q,U,A(U+vec2(k,0)));
    	Y(Q,U,A(U-vec2(0,k)));
    	Y(Q,U,A(U-vec2(k,0)));
    }
    Q = clamp(Q,vec4(0),R.xyxy);
}

Main {
    vec2 Un = (U-R*0.5)*1.0015+R*0.5 - vec2(0.98);
	vec4 a = A(U);
    float l = length(a.xy-a.zw);
    if (length(a.xy)<3.||length(a.zw)<3.){Q = B(U); return;}
    float r = sg(U,a.zw,a.xy);
    Q = (0.8+0.5*sin(.01*float(iFrame)+mod(float(iFrame),N*I)/N/I*(vec4(1,2,3,4))));
    float x = exp(-.5/(1.+.05*l)*r*r);
    vec4 b = B(Un);
    //vec4 m = 0.25*(B(Un+vec2(0,1))+B(Un+vec2(1,0))+B(Un-vec2(0,1))+B(Un-vec2(1,0)));
    //b = mix(b,m,0.01);
    Q = mix(b,Q*x,x);
    Q = min(Q,vec4(1));
    float bd = min(min(R.x - U.x - 1., R.y - U.y - 1.), min(U.x, U.y));
    Q *= 1.-exp(-bd);
    if (iFrame < 1) Q = vec4(0);
}
