#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    Q  = vec4(0);
    Q += a.x*exp(-abs(.5-vec4(1,2,3,4)));
    Q += a.y*exp(-abs(1.5-vec4(1,2,3,4)));
    Q += a.z*exp(-abs(2.5-vec4(1,2,3,4)));
    Q += a.w*exp(-abs(3.5-vec4(1,2,3,4)));
}

#define R iResolution.xy
vec2 tile (vec2 v) {return ((fract(v)*2.-1.)*sign(fract(0.5*v)*2.-1.))*0.5+0.5;}
vec4 A (vec2 U) {return texture(iChannel0,tile(U/R));}
void mainImage( out vec4 Q, in vec2 U )
{
    vec2 e = (.5+.4*vec2(sin(.3*iTime),cos(iTime)))*R;
    if (iMouse.z > 0.) e = iMouse.xy;
    Q = vec4(100)*exp(-2e2*length(U-e)/R.y);
    e = (.5+.4*vec2(sin(.9*iTime),cos(-.4*iTime)))*R;
    Q += vec4(10.+10.*sin(iTime),10.-10.*sin(iTime),-10.*sin(.25*iTime),10)*exp(-12e1*length(U-e)/R.y);
    float a = .04213*iTime, c = cos(a), s = sin(a);
    mat2 m = mat2(c,-s,s,c);
    vec3 o = vec3(U-0.5*R,0);
    o.xy *= m;
    a = .1*sin(.1*iTime); c = cos(a); s = sin(a);
    m = mat2(c,-s,s,c);
    o.yz *= m;
    for (float i = -20.;i<20.;i++) {
        vec2 u = (1.3+.3*sin(.21*iTime))*-o.xy+0.5*R+.005*R.y*i;
        Q += .115*A(u)*exp(-1.3*abs(2.-.2*i-vec4(1,2,3,4)));
    }

}
