//Image - Temporal supersampling blending.

#define BETTER_MOTION_BLUR

void mainImage(out vec4 o, in vec2 u)
{
    ivec2 iu = ivec2(u);
    #define ts(c,q) texelFetch(c,q,0)

    //comparison slider
    float sx = iMouse.w>0.?iMouse.x:iResolution.x*.5;
    if (u.x <= sx) {
        o = ts(iChannel3,iu);
        if (sx-u.x < 4.) o = vec4(.3);
    	return;
    }

    //better motion blur through a small blur on fast moving areas
	#ifdef BETTER_MOTION_BLUR
	vec4 s = vec4(0);
    for (int x = -1; x < 2; x++) {
        for (int y = -1; y < 2; y++) {
            ivec2 su = iu+ivec2(x,y);
            s += ts(iChannel3,su)+ts(iChannel1,su)+ts(iChannel2,su)+ts(iChannel3,su);
        }
    }
    s /= 9.*4.;
    #endif

    //average past 4 frames
    vec4 s0 = ts(iChannel0, iu), s1 = ts(iChannel1,iu), s2 = ts(iChannel2,iu), s3 = ts(iChannel3,iu);
    #ifdef BETTER_MOTION_BLUR
    float d = (length(s3-s0)+length(s0-s1)+length(s1-s2))/length(vec3(3));
    o = mix((s0+s1+s2+s3)/4.,s,d);
    #else
    o = (s0+s1+s2+s3)/4.;
    #endif
}

//Buf A - Stores last frame from Buf B.

void mainImage(out vec4 o, in vec2 u)
{
    o = texelFetch(iChannel0,ivec2(u),0);
}

//Buf B - Stores last frame from Buf C.

void mainImage(out vec4 o, in vec2 u)
{
    o = texelFetch(iChannel0,ivec2(u),0);
}

//Buf C - Stores last frame from Buf D.

void mainImage(out vec4 o, in vec2 u)
{
    o = texelFetch(iChannel0,ivec2(u),0);
}

//Buf D - Renders spinning block and circle.

mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}

void mainImage(out vec4 o, in vec2 u)
{
    //aspect correct uv and jitter for supersampling
    vec2 uv = u*2.-iResolution.xy;
    float sx = iMouse.w>0.?iMouse.x:iResolution.x*.5;
    if (u.x > sx) {
        const vec2 jitter[4] = vec2[](vec2(.2,-.2),vec2(1),vec2(-1),vec2(-.2,.2));
        uv += jitter[iFrame%4];
    }
    uv /= iResolution.y;

    //spinning block
    mat2 r = rot(iTime*.3);
    float d = length(max(abs(uv*r)-.2,0.));
    //floating circle
    d = min(d,length(uv+sin(iTime*vec2(1.93,.74)))-.2);
    o = vec4(max(0.,1.-max(0.,d)*1e7));
}
