#define HEART_COLOR vec3(0.6,0.01,0.25)


float smax(float a, float b, float k)
{
  float h = clamp((b-a)/k+0.5,0.0,1.0);
  return mix(a,b,h) + h*(1.0-h)*k*0.5;
}


float Heart(vec2 uv, float blr)
{

    uv *= 4.0;

    float r = 0.0;
    uv.y -= r;

    float rad = pow(0.5+0.5*sin(6.3*iTime + uv.y/25.0),4.0)+0.4; // radius
    blr *= rad;

    uv.x *= 0.7;
    uv.y -= smax(sqrt(abs(uv.x))* 0.5 ,blr,0.1);
    uv.y += 0.1 + blr*0.5;

    float dis = length(uv);

    return smoothstep(blr, blr-0.01,dis -rad);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y; // 0 <> 1.
    vec2 m = iMouse.xy/iResolution.xy;

    uv -= 0.0;                               // remap : -0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y;     //Aspect ratio

    vec3 col = vec3(0);

    float c = Heart(uv, m.y);

    col = mix(vec3(c*HEART_COLOR)/1.5,vec3(1.0,0.01,0.01),0.1);

    fragColor = vec4(vec3(col),1.0);
}
