void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //mouse
    vec2 mouse = (iMouse.xy *2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);



    // 動いてるリング Ring
    float circleHigh1 = 0.02 / abs(tan(2.0 * iTime) - distance(uv.xy, mouse));
    // 色付け Coloring    
    vec3 highCol1 = vec3(circleHigh1) * vec3(0.3, 0.3, 0.7);


    // 円 Circle
    float circle1 = 0.1 / distance(uv.xy, mouse.xy); //赤の円はマウスで動く
    float circle2 = 0.1 / distance(uv.xy, vec2(0.1, 0.0));

    // 緑と赤に色付け Coloring
    vec3 col1 = vec3(circle1) * vec3(0.7, 0.3, 0.3);
    vec3 col2 = vec3(circle2) * vec3(0.3, 0.7, 0.3);

    //合成
    vec3 color = col1 + col2 + highCol1;


    // Output to screen
    fragColor = vec4(color, 1.0);
}
