float circle(vec2 uv,float distanceAa, float distanceAb,float ditanceBa,float distanceBb,float size)
{

    float val = distance(uv, vec2(distanceAa, distanceAb)) * distance(uv, vec2(ditanceBa, distanceBb));

	val = step(val, size);

    return val;
}

float quad(vec2 uv,float posX, float posY)
{
    float quad = step(posX,uv.x)-step(posY,uv.x);
	quad = quad + step(posX,uv.y)-step(0.,uv.y)-step(posY,uv.y);
    return quad;
}

float shape(vec2 uv,float pos1,float pos2,float hands,float size)
{
    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2(pos1*ratio,pos2);
    float dist = distance(shapePos,uv)*2.;

    uv -=shapePos;
    float angle= atan(uv.y,uv.x);
    float radius = cos(2.*angle);
    radius = abs(cos(angle*(hands/2.)+iTime));

    float value = 1.-step(radius/size,dist);
    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 red = vec3(1.,0.,0.);
    vec3 green = vec3(0,1.,0.);
    vec3 yellow = vec3(1.,1.,0.);
    vec3 pink = vec3(1,.0,.0);
    vec3 bgcolor = vec3((sin(iTime)+1.)/3.,0.,0);
    float value = shape(uv,.5,.5,20.,1.);
	vec3 val2 = mix(bgcolor+.2,green, value);
    val2 = mix(val2,pink+.8,shape(uv,.06,.1,5.,5.));
    val2 = mix(val2,pink+.8,circle(uv,.1, .8,.9,.8,0.10));
    val2 = mix(val2,pink+.8,shape(uv,.94,.1,5.,5.));

    fragColor = vec4(val2,uv);
}
