#define PI 3.1415926

vec3 travel(sampler2D sampler, vec2 uv, float time) {
	float a = atan(uv.y, uv.x);
    float b = atan(uv.y, abs(uv.x));

    a -= time * .1;
    //float r = pow(pow(uv.x * uv.x, 4.) + pow(uv.y * uv.y, 4.), 1./8.);
    //float r = max(abs(uv.x), abs(uv.y));

    float r = pow( pow(uv.x*uv.x,1.) + pow(uv.y*uv.y,1.), 1./8. );

    vec2 uvL = vec2(.3 / r +  .2 * time, a / PI);
    vec2 uvR = vec2(uvL.x, b / PI);

    return textureGrad(sampler, uvL, dFdx(uvR), dFdy(uvR)).xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy)/iResolution.y;

    float r = pow(pow(uv.x * uv.x, 4.) + pow(uv.y * uv.y, 4.), 1./8.);

    vec3 col = travel(iChannel0, uv, iTime);

    col *= r;

    fragColor = vec4(col,1.0);
}
