#define PI 3.14159265

float triangle(float x)
{
    x = fract(x);
    return x < 0.5 ? x*2. : -x*2.+2.; 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)*2. - 1.;
    uv.y *= iResolution.y / iResolution.x;
    uv *= 2.5;


    float v = atan(uv.x, uv.y);
    float r = length(uv.xy);

    float x0 = uv.x;

    float a =  sin(r*5.-iTime*5.)*.1;
    float b = triangle(r*3.)*mix(-1., 1., triangle(iTime*.5))*.14;

    a +=  b * sin(iTime) - iTime;
    uv.x = x0 * cos(a) - uv.y * sin(a);
    uv.y = x0 * sin(a) + uv.y * cos(a);

    vec3 ca = 1.0*(1.*iTime+uv.x+vec3(0,2.*PI/3.,4.*PI/3.));
    vec3 sa = 1.0*(2.0*iTime+uv.x+vec3(0,2.*PI/3., 4.*PI/3.));

    v = atan(uv.x, uv.y);
    r = length(uv.xy);
    float w =  ((sin(v*3.)*(sin(float(iFrame)/50.))));
    vec3 col = 0.25 + 0.1*cos(ca) + 0.25*sin(sa);



    col /= length(uv*vec2(w));

   	//col = sqrt(col*1.);
    fragColor = vec4(col, 1.0);
   // fragColor = vec4(uv, 0, 0);
}
