#define PI 3.1415926535897932384626433832795
#define TWO_PI 6.283185307179586476925286766559

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.x;   

    float stp = smoothstep(0.0, 1.0, iTime / 20.0);
    float dis = (1.0 - stp) * 0.38 + 0.09;



    float currentDegree = TWO_PI * mod(iTime/6.0, 1.0);
    float x = cos(currentDegree) * dis;
    float y = sin(currentDegree) * dis;


    stp = smoothstep(0.0, 1.0, mod(iTime / 40.0 , 1.0));
    float lght = 3.5;

    float pct = pow(distance(st,vec2(0.2, 0.15)), distance(st,vec2(x + 0.2, y + 0.15))*lght);


    stp = smoothstep(0.0, 1.0, iTime / 15.0);
    dis = (1.0 - stp) * 0.38 + 0.09;

    currentDegree = -TWO_PI * mod(iTime/8.0, 1.0);
    x = cos(currentDegree) * dis;
    y = sin(currentDegree) * dis;

    pct += pow(distance(st,vec2(0.45, 0.4)), distance(st,vec2(x + 0.45, y + 0.4))*lght);

	stp = smoothstep(0.0, 1.0, iTime / 30.0);
    dis = (1.0 - stp) * 0.5 + 0.15;

    currentDegree = TWO_PI * mod(iTime/5.0, 1.0);
    x = cos(currentDegree) * dis;
    y = sin(currentDegree) * dis;

    pct += pow(distance(st,vec2(0.8, 0.15)), distance(st,vec2(x + 0.8, y + 0.15))*lght);


    vec3 color = vec3(pct);

    fragColor = vec4(color,1.0);
}
