#define N 400.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 2.0*( fragCoord.xy / iResolution.xy ) - 1.0;
    p.x *= iResolution.x/iResolution.y;

	float color = 0.0;
	float mk=floor(45.*iMouse.x);	// Click to change number of spirals
	float ft=(3.141/180.)*mk;
	for(float t=0.;t<N;t++)
	{
		float an=t*ft+5.*iTime;
		float rg=(t/N+abs(cos(t*2.)))/2.;
		float kx=t/N;
		float x=(kx*rg)*cos(an);
		float y=-0.5+t/N+(rg*.3)*sin(an);
		vec2 r=vec2(x,y);
		float d=abs(0.02+0.01*sin(an+3.141));
		if(distance(p,r)<d)
				color=d*30.;
	}


	fragColor = vec4( vec3( color ), 1.0 );
}
