// Created by Charles Humphrey 26/09/2019
// ShaderToy version of the DeRezzed  post process effect I have in my MonoGame Repo
// https://github.com/NemoKradXNA/Randomchaos-MonoGame-Samples

float  numberOfTiles = 256.0;
float FadeSpeed = 0.75;

vec2 fmod(vec2 x, vec2 y)
{
 	return x - y * trunc(x/y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 im = iMouse.xy / iResolution.xy;

    float v = mix(-numberOfTiles,numberOfTiles, clamp(sin(iTime * FadeSpeed),0.0,1.0));

    vec2 size = vec2(1.0 / v) ;

    uv = (uv - fmod(uv, size)) + (size / 2.0).x;

    fragColor = texture(iChannel0,uv);

}
