// v1.1

#define t mod(iTime,2.)
#define ease(t) smoothstep(0.,1.,t)
#define C(off,var) var = vec2(uv); \
	var.x += mix(ease(x-.5+off),ease(x),ran11(uv.y+sin(floor(iTime)))); \
    var.y += mix(ease(y-.5+off),ease(y),ran11(uv.x-cos(floor(iTime)))); \
    var = fract(var)-.5;
#define S(c) col.c = smoothstep(0.3,0.32,length(c));

float ran11(float n) {
    return fract(sin(n*12346.131)*534125.72);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.x;
    uv *= 20.;

    float x = min(1.,t)*1.5;
    float y = (max(1.,t)-1.)*1.5;
    uv.x = mod(floor(uv.y),2.)==1.&&t<1.?-uv.x:uv.x;
    uv.y = mod(floor(uv.x),2.)==1.&&t>1.?-uv.y:uv.y;

    vec2 r,g,b = vec2(0.);

    C(-.1,r) C(0.,g) C(.1,b)

    vec3 col = vec3(0);
    S(r) S(g) S(b)

    fragColor = vec4(col,1.);
}
