// code golfed variant of https://shadertoy.com/view/ltcXzs

#define C(U) texture( iChannel0, U/H/16. + fract( vec2( int(++t), 15-int(t)/16 ) / 16. ) ).w - .5//
void mainImage(out vec4 O, vec2 U) {
    float H = iResolution.y, t = 3.*iTime;
    O +=  H* mix( C(U), C(U), fract(t) );
}// variant: smoothstep(0.,1.,fract(t))
