float rand(float n){return fract(sin(n) * 43758.5453123);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    vec3 col = vec3(0.0);
    vec2 center = iResolution.xy * 0.5;
    float size = iResolution.y;
    float t = iTime*0.125;
    for(float i = 0.0 ; i < 20.0 ; i+=1.0) {
        float s = 1.0+rand(i)*2.0;
        float y = t*s+i*7.3;
        float phase = fract(y);
        float id = rand(floor(y));
        float angle = id*6.28318;
       	float len = (0.04+phase*0.96) * size;
		float alpha = 1.0 - pow(2.0*abs(0.5-phase), 4.0);
        vec2 target = vec2(center.x+sin(angle)*len, center.y+cos(angle)*len);
        float amount = (0.5+0.4*sin(id*2.0+phase*id*9.0))*alpha*pow(length(uv - target)*0.125, -1.0*(1.0-phase*0.5));
        col += 0.7*amount * vec3(1.0, 0.8, s*0.25);        
    }
    fragColor = vec4(col,1.0);
}
