#define PI 3.14159
#define r(uv,lg) ( length(uv) / norm(lg))
#define norm(v) ( (v) / iResolution.x )
#define animate(t) ( 70.*sin(t)*step(-PI, -mod(t,2.*PI)) )

float sdBox( in vec2 p, in vec2 b ) {	//iq
    p = p * iResolution.x/800.-vec2(.0,.07);
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0) - .3;
}
float getcoef(float theta) {
	theta /= 2.*PI;
    return theta*smoothstep(1.,.99,theta);	//antialiasing
}
float circleloading(vec2 uv, float angle) {
    float r = r(uv, 400.); 
	return mix( smoothstep(.07, .05, abs(r-.25)), .0, getcoef(angle) );
}
float pointsloading(vec2 uv) {
	float value = smoothstep( .1, .08, r(uv - norm( vec2(.0, animate(5.*iTime)) ), 450.) )
        		  + smoothstep( .1, .08, r(uv - norm( vec2(200., animate(5.*iTime-.6)) ), 450.) )
        		  + smoothstep( .1, .08, r(uv - norm( vec2(-200., animate(5.*iTime+.6)) ), 450.) );
    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float angle = atan(uv.y, uv.x);    
    angle = mod(angle + PI + 5.*iTime, 2.*PI);

    vec3 color = vec3(1.);
    color *= mix( circleloading(uv, angle), 
                  smoothstep( .01, .0, sdBox( uv, vec2(.12,.0) ) ) * .7 * (1. - pointsloading(uv)),
                  mod(floor(iTime/PI), 2.) );

    fragColor = vec4(color * .9 + .1, 1.);
}
