float rand( vec2 n ){return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 45.5453);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv *= 4.;
    float dist = 1.;

    for(float i = -1.; i < 2.; i++)
    {
        for(float j = -1.; j < 2.; j++)
        {
            vec2 coord = floor(vec2(uv.x, uv.y));
            vec2 loopPos = vec2(i,j);
			float temp = distance(vec2(uv + loopPos),
                                  vec2(coord + rand(vec2(coord - loopPos)))
                                 );
            if(temp < dist)
                dist = temp;
        }
    }
    vec3 col = 1. -vec3(dist);

    fragColor = vec4(col,1.0);
}
