void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    uv.x -= 0.8;

    //Noise Sample 1
    vec2 NoiseUV = uv;
    NoiseUV = (iTime*0.01)-NoiseUV;
    NoiseUV *= 0.4;

    vec4 noise = texture(iChannel0,NoiseUV);


    //Noise Sample 2
    vec2 NoiseUV2 = uv;
    NoiseUV2 = (iTime*-0.01)-NoiseUV2;
    NoiseUV2 *= 0.4;

    vec4 noise2 = texture(iChannel0,NoiseUV2);

    //vec4 finalnoise = mix(noise,noise2,0.5);
    vec4 finalnoise = noise+noise2;
    finalnoise = normalize(finalnoise);

    //Original Noise Sample

    vec2 originaluv = uv;
    originaluv += iTime*0.005;
    vec4 originalnoise = texture(iChannel0,originaluv*(0.8,0.8));

    //Composite final on top of original
    finalnoise = finalnoise*5.+originalnoise;
    finalnoise = normalize(finalnoise);

    //distort original with the finalnoise to make the reveal texture
    vec2 revealuv = uv;
    vec4 revealnoise = texture(iChannel0,(revealuv+finalnoise.xx)*(0.1,0.1)) * (finalnoise*6.);


    //Coloring 
    //yellow = 255,210,40 
    //light red = 248,58,12 
    //dark red = 205,18,31
    if (revealnoise.r > 0.9 && revealnoise.r < 0.95 || revealnoise.r < 0.86 && revealnoise.r > 0.8 || revealnoise.r > 0.6 && revealnoise.r < 0.8 || revealnoise.r > 0.36 && revealnoise.r < 0.4)
    {
         revealnoise.r = 248./255.;
        revealnoise.g = 58./255.;
        revealnoise.g = 12./255.;
    }
    else if (revealnoise.r > 0.86 && revealnoise.r < 0.9 || revealnoise.r > 0.4 && revealnoise.r < 0.6)
    {
        revealnoise.r = 1.;
        revealnoise.g= 210./255.;
        revealnoise.b=40./255.;
    }
    else 
    {
        revealnoise.r = 205./255.;
        revealnoise.g = 18./255.;
        revealnoise.g = 31./255.;
    }



    col = revealnoise.rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
