    vec2 p = vec2(0.);
    fragColor = vec4(0.);
    for (int i=0; i<5000; i++) {
        p = vec2(p.x * p.x - p.y * p.y, p.y * p.x + p.x * p.y) + 1.5 * (2.*fragCoord-iResolution.xy)/iResolution.y + vec2(-.5, 0.);
        if (length(p) > 1000.) {
            fragColor = vec4(float(i)/20.*vec3(.5,.5,.7), 1.);
            break;
        }
    }}
