void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = 1.0 - uv.x;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec4 data = texture(iChannel0, uv).rgba;

    vec4 new_data = vec4(0.0, 0.0 , data.z, 0.0);


    vec3 source_image = texture(iChannel1, uv).rgb;
    vec2 image_diff = vec2(0.0);
    float edges =  0.0;


    vec2 speed_diff = vec2(0.0);

    for (int x = -1; x<=1; x++) {
        for (int y = -1; y<=1; y++) {
            vec2 dir = vec2(float(x), float(y)) * 1.0;
            vec4 data_B = texture(iChannel0, uv + dir / iResolution.xy).rbga;
            vec4 data_diff = data_B - data;

			new_data.xy += data_diff.xy * dir;
            new_data.z += step(0.1, length(data_diff.xy)) * dot(data_diff.xy, -dir);

            image_diff += dir * length(texture(iChannel1, uv + dir / iResolution.xy).rbg);
        }
    }
    new_data.w  = length(image_diff);
    new_data.w  = smoothstep(0.2, 0.25, new_data.w);
    new_data.w  = mix(data.w, new_data.w, 0.25);


    //new_data = clamp(new_data, vec4(-1.0), vec4(1.0));

    new_data.xy = mix(data.xy, new_data.xy * 0.9, 0.75);
    new_data.z = mix(data.z, new_data.z  * 1.0 / 5.0, 0.75);
    //new_data.z = max(0.0, new_data.z);

    new_data = clamp(new_data, vec4(-1.0), vec4(1.0));

    new_data = mix(new_data, vec4(0.0, 0.0, 1.0, new_data.w), new_data.w );


    fragColor = new_data;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;

    vec4 data = texture(iChannel0, uv).rgba;
    vec3 col = texture(iChannel1, uv).rgb;
    vec3 col_bg = texture(iChannel2, uv + 10.0 * data.xy / iResolution.xy).rgb;

    col = mix(col, col_bg,  1.0 - data.w);


    fragColor = vec4(col,1.0);
}

