const int arraysize = 2;
const float loaddist =10.0;
struct solid
{
	vec4 color;
    float dist;
    float thick;
};
void Sort(inout solid first[arraysize]){
    int size = arraysize;
    // 配列を前から順にヒープ構造に追加する
    for (int i=1; i<size; ++i){
        int j = i; // ヒープの末尾に追加
        // 親ノードが子ノードより大きくなるように並び替え
        while (j > 0){
            if (first[(j-1)/2].dist < first[j].dist){
                solid w = first[(j-1)/2];
                first[(j-1)/2] = first[j];
                first[j] = w;
               j = (j-1)/2;
            } else {
               break;
            } 
        }
    }

    for (int i=size-1; i>0; --i) {
        solid w = first[0];
        first[0] = first[i];
        first[i] = w; // 根を後ろに送る
        int j = 0;
        int k = 0;
        // ヒープの条件を満たすように入れ替え
        while(true){
            int left = 2*j + 1;
            int right = 2*j + 2;
            if (left >= i) break;
            if (first[left].dist > first[k].dist) k = left;
            if ((right < i) && (first[right].dist > first[k].dist)) k = right;
            if (k == j) break;
            solid w = first[k];
            first[k] = first[j];
            first[j] = w;
            j = k;
        }
    }
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
float map(vec3 p, int num){
    //float d = length(p-vec3(0.0,0.0,1.0)) - 0.25;
    float d =  loaddist;
    if(num == 0){
        d = sdBox(p - vec3(0.0,0.0,0.2), vec3(0.3,0.3,0.3));
    }
    if(num == 1){
        d = sdBox(p - vec3(0.2,0.05,-0.4), vec3(0.2,0.15,0.2));
    }
	return d;
}
float trace(vec3 o, vec3 r, int num){
	float t = 0.0;
    for(int i = 0; i < 32; ++i){
    	vec3 p = o + r * t;
        float d = map(p, num);
        t += d * 0.5;
    }
    return t;
}
vec4 col = vec4(1.0,1.0,1.0,1.0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 uv1 = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv, 1.0));

    float the = iTime * 0.25;
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    vec4 fc = vec4(0.0, 0.0, 0.0, 0.0);
    solid solids[arraysize];
    solids[0].color = vec4(1.0, 0.0, 1.0, 0.8);
    solids[1].color = vec4(0.0, 1.0, 1.0, 0.7);

    for(int i = 0; i < arraysize; i++){
    	vec3 o = vec3(-1.0* sin(-the), 0.0, -1.0 * cos(-the));
    	float t = trace(o, r, i);
        solids[i].dist = t;
    	solids[i].thick = trace(o+r*loaddist, -r, i);
    	solids[i].thick = loaddist - t - solids[i].thick;
        solids[i].thick=max(solids[i].thick,0.0);
    }
    Sort(solids);
    for(int i = 0; i < arraysize; i++){
    	if(solids[i].thick!=0.0){
    		solids[i].thick += 0.5;
    		col = solids[i].color;
        	float fog = 1.0 - pow(col.w,solids[i].thick);
            vec3 col3 = fc.rgb * fc.w + col.rgb * (1.0 - fc.w) * fog;
    		fc = vec4(col3, fc.w + (1.0 - fc.w) * fog);
            fc.rgb /= fc.w;
    	}
    }
    vec3 col3 = fc.rgb * fc.w + vec3(0.2) * (1.0 - fc.w);
    fc = vec4(col3, 1.0);
	fragColor = vec4(fc);

    //solid test[arraysize];
