void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.y;
    float slow = 0.10;
    float scale = 100.00/(iMouse.x*iMouse.x);
    if(scale>3.00){scale=3.00;}
    float cx = -1.782/scale;//-10.00+((iMouse.x)/iResolution.x);
    float cy = -0.50;//+((iMouse.y)/iResolution.y);
    float limit =3.2 + 10.00*(sin(iTime)+1.00);
    float ax=(cx+uv.x)*scale;
    float ay=(cy+uv.y)*scale;
    float a1=ax;
    float b1=ay;
    int lp=0;
    float count = 251.00*(sin(iTime)+1.00);
    int icount = 4 + int(floor(count));
    for(int i=0;i<icount;++i){
        float a2=(a1*a1)-(b1*b1)+ax;
        float b2=2.0*a1*b1+ay;
        a1=a2;
        b1=b2;
        lp=i;
        if(((a1*a1)+(b1*b1))>limit){break;}
    }
    vec3 col = vec3(sin(a1*b1),sin(a1/b1),uv.y);
    if(lp==(icount-1)){
        col = vec3(0.0,0.0,0.0);
        //col = vec3(sin(((a1*a1)/(b1*b1))/(iTime/(1.00*slow))), sin((a1/b1)/(iTime/(2.00*slow))),sin(((b1/a1)/(a1/b1))/(iTime/(3.00*slow))));
    }
    fragColor = vec4(col,1.0);
}
