// Fork of  https://shadertoy.com/view/wd3GR7   
// funny: ceil(U) instead of floor create artifacts at some resolutions


void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = 3.* (u+u-R)/R.y, A = mod(floor(U),2.);
    A = abs( 3.1*(fract(U)-.5) * mat2(cos(vec4(0,33,11,0) +(A.x==A.y?1.:-1.)*iTime))),
    O += clamp( .5 - ( min(abs(.9*A-.5*A.x),A.x) -.2 ) /30. *R , 0., 1. ).y;
}
