void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;

    vec2 q = p-vec2(0.5,0.5);
    q.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(1.0,0.7,1.0);

    float r = 0.2 + 0.1*cos(atan(q.y,q.x)*(iTime*8.0));
    col *= smoothstep(r+0.2, r+0.02, length(q));

    fragColor = vec4(sin(r+col.x), sin(col.y-r), cos(col.z*r),1.0);
}
