void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Pi, the ratio of a circle's circumference to its diameter.
	const float M_PI = 3.14159265358979323846264338327950288;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Draw circle
    vec3 bg = vec3(0.1, 0.1, 0.1);
    vec3 white = vec3(0.9);
    vec3 col = bg;
    //vec2 center = vec2(200.0, iResolution.y - 150.0);
    vec2 center = iResolution.xy / 2.;
    float l = length(fragCoord-center);
    //float radius = ((sin(iTime*3.0)+1.0) * 40.0);
    float radius = 125.0;
    float am = (l - radius);
    col = mix(white, bg, am);

    // Only draw a pizza slice of the circle
    float startAngle = atan(iMouse.y - center.y, iMouse.x - center.x);
    float span = (sin(iTime) + 1.) * M_PI;

    float angle = atan(fragCoord.y - center.y, fragCoord.x - center.x);
    float aim = startAngle - span / 2.;

    float range = span / 2.;

    float arcL = l * angle;
    float arcLAim = l * aim;
    float arcRangeLength = l * range;

    float arcDist = abs(arcL - arcLAim);
    float halfCircumf = l * M_PI;
    if(arcDist >= halfCircumf)
    {
        arcDist -= halfCircumf * 2.;
    	arcDist = abs(arcDist);
    }
    float arcDistDist = (arcDist - arcRangeLength);
    float sdfCombined = max(am, arcDistDist);
    sdfCombined = abs(sdfCombined)-10.;
    sdfCombined = clamp(sdfCombined, 0., 1.);
    col = mix(white, bg, sdfCombined);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
