// BASIC RAY-MARCHING CLASS DEMO! https://www.shadertoy.com/view/wdKyD3
//
// Numerous methods borrowed from iq articles:
//   https://iquilezles.org/www/index.htm
// 
// 3D signed distance functions:
//   https://iquilezles.org/articles/distfunctions
// 
// Shadows:
//   https://iquilezles.org/articles/rmshadows
// 
// Ray-marching SDFs:
//   https://iquilezles.org/articles/raymarchingdf
// 
// Normals for SDFs:
//   https://iquilezles.org/articles/normalsSDF

float opUnion( float d1, float d2 ) {  return min(d1,d2); }
vec4  opUnion(vec4 d1, vec4 d2) {return (d1.x < d2.x ? d1 : d2); }
float opIntersection( float d1, float d2 ) { return max(d1,d2); }
vec4  opIntersection( vec4  d1, vec4  d2 ) {return (d1.x > d2.x ? d1 : d2); }

// subtracts d1 from d2
float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }
vec4  opSubtraction( vec4  d1, vec4  d2 ) { return opIntersection(vec4(-d1.x,d1.yzw), d2);}
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }
float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }
float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }
float opOnion( in float sdf, in float thickness ){ return abs(sdf)-thickness;}


// 3D gradient noise methods. 
// https://www.shadertoy.com/view/Xsl3Dl
vec3 hash( vec3 p ) // replace this by something better
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}
const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );


vec3 rotate_x(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		+1.0, +.0, +.0,
		+.0, +ca, -sa,
		+.0, +sa, +ca);
}
vec3 rotate_y(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		+ca, +.0, -sa,
		+.0,+1.0, +.0,
		+sa, +.0, +ca);
}
vec3 rotate_z(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		+ca, -sa, +.0,
		+sa, +ca, 0.,
		+.0, +.0, 1.);
}

// iq distance functions, 
// See https://iquilezles.org/articles/distfunctions
float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdSphere(vec3 p, float radius )
{
  return length(p)-radius;
}

float sdTorus(in vec3 p, in vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}
float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}
float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}
float sdPlane( vec3 p, vec3 n, float h )
{
  // n must be normalized
  return dot(p,n) + h;
}
float sdBoundingBox( vec3 p, vec3 b, float e )
{
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

// not exact
float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

// See https://www.shadertoy.com/view/Wdjfz3
float sdEgg( in vec2 p, in float ra, in float rb )
{
    const float k = sqrt(3.0);

    p.x = abs(p.x);

    float r = ra - rb;

    return ((p.y<0.0)       ? length(vec2(p.x,  p.y    )) - r :
            (k*(p.x+r)<p.y) ? length(vec2(p.x,  p.y-k*r)) :
                              length(vec2(p.x+r,p.y    )) - 2.0*r) - rb;
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

vec3 bendPoint( in vec3 p , float amt)
{
    float k = amt; //  some amount, e.g., 10.
    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xy,p.z);
    return q;
}

// Function to y-axis revolve a 2D SDF into a 3D SDF.
vec2 revolvePoint( in vec3 p, float radius )
{
    return vec2( length(p.xz) - radius, p.y );
}


/// SCENE PARAMETERS (feel free to add your own):
const vec3 center = vec3(0., -0.15, -8.0);
const vec3 red			 = vec3(238./255.,21./255.,21./255.);
const vec3 off_white 	 = vec3(250./255.,250./255.,250./255.);
const vec3 ground_col = vec3(217./255.,179./255.,130./255.);
const vec3 dark_grey = vec3(0.15,0.15,0.15);
const vec3 teal = vec3(46./255.,105./255.,108./255.);

// SDF implementation for our scene. 
// Returns 4 values: (sdf, Cd)
//   .x: SDF distance, 
//   .y: Cd.r
//   .z: Cd.g
//   .w: Cd.b
// NOTE: You could return other things for yzw, e.g., y:objectID int to set more colors in "render."
vec4 mapV4(in vec3 p)
{
    // SDF vector (sdf, r,g,b) to be returned:
    vec4 sdf = vec4(100000., -1., 0., 0.);

    float mx = iMouse.x/iResolution.x;

    // TODO: REPLACE THE FOLLOWING WITH YOUR SCENE CODE:

    // Translate coordinate to pumpkin center:
    vec3 c = center;
    p -= c;


    {// BODY:
        vec3 a = p;
        sdf.x = sdSphere(p,1.);
        if(a.y > 0.) 
        {
            sdf.yzw = teal;
        }
        if(a.y < 0.) 
        {
        	sdf.yzw = off_white;
        }

        // Subtracts middle ring
 		float ring = sdBox(a,vec3(2.,0.05,2.));
        vec4  ringSub = vec4(ring, off_white);
        sdf = opSubtraction(ringSub, sdf);

        float innerSD = sdSphere(a,0.98);
        vec4 innerSDCombine = vec4(innerSD, dark_grey);
        sdf = opUnion(innerSDCombine, sdf);
        {//BUTTON
            float innerButton = sdRoundedCylinder(rotate_x(a,1.5707963268)-vec3(0.,-.95,0.0),0.08,0.01,0.08);
        	vec4 innerButtonCombine = vec4(innerButton, off_white);
        	sdf = opUnion(innerButtonCombine,sdf);

            float button = sdRoundedCylinder(rotate_x(a,1.5707963268)-vec3(0.,-.93,0.0),0.1,0.01,0.08);
        	vec4 buttonCombine = vec4(button, off_white);
        	sdf = opUnion(buttonCombine,sdf);

        	float outerButton = sdRoundedCylinder(rotate_x(a,1.5707963268)-vec3(0.,-.93,0.0),0.11,0.01,0.08);
        	vec4 outButtonCombine = vec4(outerButton, dark_grey);
        	sdf = opUnion(outButtonCombine,sdf);
        }
        //Stripes
        if(a.y > 0.05) {
        	float stripe1SD = sdTorus(rotate_z(rotate_x(a,1.5707963268),0.9),vec2(1.,0.06));
            vec4 stripe1 = vec4(stripe1SD,dark_grey);
            sdf = opUnion(stripe1,sdf);
            float stripe2SD = sdTorus(rotate_z(rotate_x(a,1.5707963268),-.9),vec2(1.,0.06));
            vec4 stripe2 = vec4(stripe2SD,dark_grey);
            sdf = opUnion(stripe2,sdf);
            if(a.z < 1.01) {
            	float stripe3SD = sdTorus(rotate_x(rotate_z(rotate_x(a,1.5707963268),1.5707963268),0.8),vec2(1.,0.06));
                vec4 stripe3 = vec4(stripe3SD,dark_grey);
                sdf = opUnion(stripe3,sdf);
                float stripe4SD = sdTorus(rotate_x(rotate_z(rotate_x(a,1.5707963268),-1.5707963268),0.8),vec2(1.,0.06));
                vec4 stripe4 = vec4(stripe4SD,dark_grey);
                sdf = opUnion(stripe4,sdf);
            }
        }


    }


	{// TABLE: 
        //float d = sdPlane(p, normalize(vec3(0.,1.,0.)), 1.2);
        //float d = sdBox(p-vec3(0.,-1.3,0.), vec3(5., 0.1, 10.));
        float d = sdRoundedCylinder(p-vec3(0.,-1.2,0.), 1.2, 0.1, 0.1);
        vec4  ground = vec4(d, ground_col);
		sdf = opUnion( sdf, ground );       
    }

    return sdf;
}


// Scalar SDF implementation for our scene
float map(in vec3 p)
{
    return mapV4(p).x;// just sdf value
}


// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{

#if 0
    vec2 e = vec2(1.0,-1.0)*0.5773*0.001;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
#else
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.001*e);
    }
    return normalize(n);
#endif    
}

// Vector raymarch: 
// .x   : t* value of surface, or -1 if didn't hit anything.
// .yzw : pass-thru from mapV4.yzw
// 
vec4 raymarchV4(in vec3 ro, in vec3 rd) 
{
    float t = 0.0;
    for(int i=0; i<200; i++) {
        vec3  rt = ro + rd*t;
        vec4  f  = mapV4(rt);
		float d  = f.x;

        if(d<0.001) {// We hit it or went too far
            f.x = t; // replace .x's sdf with t*
            return f;
        }
        else {// keep marching
         	t += 0.95*d; // conservative/smaller step for inexact/warped SDFs
        }

        if(t > 100.) {
            f.x = -1.;// -ve t* --> didn't hit anything
            return f;
        }
    }

	return vec4(-1., vec3(0.)); // didn't hit anything
}

/// Returns t* value of surface, or -1. if didn't hit anything.
float raymarch(in vec3 ro, in vec3 rd) 
{
    return raymarchV4(ro,rd).x;  
}

float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.;
        t += 0.95*h;//can do slightly less for not-quite-SDFs
    }
    return 1.0;
}
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    float ph = 1e20;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.0;

        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += 0.95*h;//slightly less for not-quite-SDFs
    }
    return res;
}

vec3 render(in vec3 ro, in vec3 rd) 
{
    vec3 col = vec3(0.,0.,0.);//background
	vec2 mouse = iMouse.xy/iResolution.xy;         

    vec4  ray = raymarchV4(ro, rd);//compute distance along ray to surface
    float t   = ray.x;
    vec3  Cd  = ray.yzw;// can passthru other shading values, e.g., objectID, but we did color.

    if(t>0.0) {//hit surface --> shade it:   

	    vec3  p  = ro + rd*t; // point on surface
        vec3  N  = calcNormal(p); // sdf normal

        // DETERMINE MATERIAL COLOR: (todo: all orange for now)
        //vec3  Cd = vec3(231./255., 111./255., 3./255.); // diffuse color

        // DIRECTIONAL LIGHT:
        vec3  posL   = ro+ 30.*vec3(mouse.x-0.5, mouse.y-0.5,0.);// light at eye (safe!)
        vec3  L      = normalize(posL - p);
        vec3  CL     = vec3(2.*sin(iTime+.5)+0.5,sin(iTime-0.5)+0.5,sin(iTime+1.5)+0.5); // vec3(1.); directional light color
        float LdotN  = clamp(dot(L,N), 0., 1.);
        //float shadL  = shadow(p, L, 0.01, 20.);
        float sshadL = softshadow(p, L, 0.01, length(posL-p), 2.);
	    col = Cd * CL * LdotN * sshadL; // * occ;

    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 mouse = iMouse.xy/iResolution.xy;   

    vec2 uv  = (fragCoord - 0.5*iResolution.xy)/iResolution.y; // normalized coordinates (unit height)

    vec3 pix = vec3(uv,     0.);    // position of virtual pixel (Z=0 depth)
    vec3 ro  = vec3(0., 0., 3.);    // ray origin (EYE POSITION).. controls field-of-view
    vec3 rd  = normalize(pix - ro); // ray direction

    vec3 col = render(ro, rd);

    // Output to screen
    fragColor = vec4(col,1.0);
}
