void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0);
	uv-=.5;
	uv.x*=iResolution.x/iResolution.y;

	uv+=vec2(sin(time*.6)*.25,cos(time*.6)*.25);
	float x=0.;
	float y =0.;

	float backCircle  =  smoothstep(.3,.29,length(uv -vec2(x,y)));
//right side
	float innerCircle1 =0.;
	float innerCircle2 =0.;
	if(uv.x<-.07){
	innerCircle1 = -smoothstep(.28,.26,length(uv));
	innerCircle2 = -smoothstep(.25,.23,length(uv));
	}	

	float line = 0.;
	if(abs(uv.y)<.264 )
	line = -smoothstep(.02,.01,length(uv.x+.06));

//left side
	float innerCircle3 = .0;
	float innerCircle4 = .0;
	float line2 = .0;
	if(uv.x>.07){
	innerCircle1 = -smoothstep(.28,.26,length(uv));
	innerCircle2 = -smoothstep(.25,.23,length(uv));
	}	

	line2 = .0;
	if(abs(uv.y)<.264 )
	line2 = -smoothstep(.02,.01,length(uv.x-.06));

	float line3 = .0;
	if(abs(uv.x)<.05 )
	line3 = -smoothstep(.02,.01,length(uv.y));



	col+=line3;
	col+=line;
	col+=line2;
	col+=backCircle * vec3(.845,.59645,.1);
	col+=(innerCircle1-(innerCircle2));

	//col+=innerCircle2;
	fragColor = vec4( col, 1.0 );


    // Time varying pixel color

    // Output to screen
}
