mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,-s,s,c);}
float sdTorus( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float dist(vec3 p){
    p.xz*=rot(p.y*4.+iTime);
    //p.yz*=rot(iTime);
    p.yz*=rot(3.14/2.);
	return sdTorus(p, vec2(.5,.2+.1))*.8;//length(p)-1.;
}

void mainImage( out vec4 fragColor, in vec2 FC )
{
    vec2 uv=(FC*2.-iResolution.xy)/iResolution.y;
    float e,d=0.;
    vec3 p,rd=vec3(uv,1);
    int i;
    for(i=1;i<11;i++){
        p=rd*d-vec3(0.,0.,1.5);
        d+=e=dist(p);
        if(e<.01)break;
    }
    //float col1=1./d;
     vec3 col = cos(iTime+uv.xyx) + 0.5*cos(iTime+uv.xyx+vec3(0,2,4))/d;
    fragColor = vec4(vec3(col),0.);
}
