#define PI 3.1415
#define TWO_PI (2.0*PI)

float random(vec2 st) {
    return fract(
        43758.5453123 * 
        sin(dot(st.xy, vec2(12.9898, 78.233))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    float y = 0.0;

    { // Cloud shape
    	float a = TWO_PI*uv.x + iTime; // frequency
    	float b = 2.5; // phase
    	float c = 20.0; // amplitude

        // Shape function using sum of sines
   	 	y = sin(a) + sin(a/2.0) + sin(a/3.0) + b;
    	y /= c;
    }

    vec3 col = vec3(0.0);

    { // Border fuzz
    	float z = uv.y - y;
    	float a = 0.05;
        float b = 0.1;
        float e = 0.15; // epsilon factor to blur seams
        float r = random(uv);

    	col = z < a ? vec3(1.0) : vec3(0.0);
    	if (abs(z) < a) {
            float f = (b-a) / (z-a);
            f = f*f*f + e;

    		col += f * r;
    	}
    }

    fragColor = vec4(col, 1.0);
}
