mat2 rot(float a){
    float s=sin(a);
    float c=cos(a);
    return mat2(c,-s,s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv*=rot(iTime);
    vec3 col=vec3(1.,0.,0.);
    float width=.02;
    vec2 z=uv*10.*(abs(fract(iTime)-.5)+0.2);
    vec2 f=fract(z)-.5;

	float len=length(vec2(abs(f.x)*1.1,f.y)-vec2(0.5,0.45))-.43;
    float color=smoothstep(0.01,-0.01,abs(len)-width);
    float bar=smoothstep(.01,-.01,abs(f.x)-width)*smoothstep(-0.01,0.01,f.y);
    color+=bar;


    if(mod(floor(z.x),2.)==0.)col+=vec3(color);
    if(abs(f.x)>.48 || abs(f.y)>.48)col=vec3(.0,.0,.0);
    fragColor = vec4(col,1.0);
}
