float radio = 1000.0;
float rimSize = 4.0;
vec2 handView = vec2(10.0,100.0);	
float scale = 0.4;		

vec2 curvatureGenerator(vec2 toPow,  float dis){
    float x = dis/radio;
    return toPow*(1.0-x)*exp(-2.0*x*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = texture(iChannel0, uv).rgba;

    vec2 center = iMouse.xy;

    float dis = distance(fragCoord.xy, center);

    vec2 disV = fragCoord.xy - center;

    if(dis > radio && dis < radio + rimSize){
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } 

    if(abs(disV.x) < (handView.x / 2.0) && abs(disV.y + radio) < ((handView.y))&& dis >= radio + rimSize)
    {
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }

    if(dis < radio){
        //dis = distance(fragCoord.xy, center + radio*0.5*vec2(cos(iTime),sin(iTime)));

        vec2 trueUV = (fragCoord.xy - (curvatureGenerator(disV,dis) *scale) ) / iResolution.xy;
    	fragColor = texture(iChannel0, trueUV).rgba;
    }
}
