void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 p=(fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);   
    float u=abs(sin((atan(p.y,p.x)-length(p)+iTime*0.3)*10.0)*0.5)+0.2;
    float t=u-length(p);
    fragColor = vec4(smoothstep(1.5,0.,abs(t)/fwidth(t)));
}
