vec2 polar(vec2 xy) {
  return vec2(
      xy.x * cos(xy.y),
      xy.x * sin(xy.y)
  );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy/iResolution.xy;
   // vec2 center = iMouse.xy/iResolution.xy;

    vec4 tex = texture(iChannel0, uv);  
    float sound = texture(iChannel1, polar(uv)).r;
    uv.x += 0.5;
    //uv.x *= 2.0;
    vec2 pos = vec2(uv.x * sound, uv.y);
    vec4 col = vec4(0, 1, 0, 1) * smoothstep(polar(pos).x, 0.5, 0.01);

    fragColor = col;
}
