vec3 colorA = vec3(0.2509,0.1568,0.2901);
vec3 colorB = vec3(0.4509,0.2627,0.2941);
vec3 colorC = vec3(0.7019,0.3019,0.1450);
vec3 colorD = vec3(0.9411,0.4941,0.0274);
vec3 colorE = vec3(0.9686,0.8705,0.3333);

vec3 red = vec3(225./255., 95./255., 60./255.);
vec3 white = vec3(1.,1.,1.);
vec3 blue = vec3(0.,128./255.,1.);
vec3 black = vec3(0.,0.,0.);
vec3 pink = vec3(255./255.,192./255.,203./255.);


vec3 rgb(float r, float g, float b) 
{
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.0;

	if ((uv.x > pos.x - width / 2.0) && (uv.x < pos.x + width / 2.0)
		&& (uv.y > pos.y - height / 2.0) && (uv.y < pos.y + height / 2.0)) {
		t = 1.0;
	}
	return vec4(color, t);
}

mat2 scale2d(vec2 value)
{
    return mat2(value.x,0.,0.,value.y);
}
mat2 rotate2d(float angle)
{
    return mat2(cos(angle), - sin(angle),sin(angle),cos(angle));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv2 = fragCoord.xy / iResolution.xy;

    float x = sin(iTime * 1.5 ) ;

    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;

    vec2 pos = vec2(0.5 * ratio,0.5);
    uv -= pos;

   uv2 *= rotate2d(sin(iTime));
    uv *= scale2d(vec2(1.0,1.0));

    uv+=pos;
    vec2 shapePos = vec2(.5 * ratio, .5);
    float dist = distance ( shapePos,uv) ;

    uv -= shapePos;

	float angle = atan(uv.y, uv.x)  + iTime ;
    float radius = cos(5. * angle + iTime ) * .3;

    // rectangle Properties
	vec2 center = vec2(0., 0.);
	float width = .2;
	float height = .2;

    // Background layer
	vec4 bg = vec4(rgb(1.0, 1.0, 1.0), 1.0);

	// Rectangle
	vec3 col = rgb(255.0, 255.0, 0.0);
	vec4 rect = rectangle(uv, center, width, height, col);

	// Blend the two
	vec3 color = mix(vec3(0.), white, rect.a);
    color = mix(color,white,rectangle(uv,vec2(.0,.0),.08,.3,col).a);
     color = mix (color, white,rectangle(uv,vec2(.0,-.36),.08,.4,col).a);

    float value = 1. - step(radius,dist);

    color = mix(color,red,value);
    //
    vec3 color2 = mix(colorA,colorB,uv.y);
    color2 = mix(color,colorC,uv.y);
	color2 = mix(color,colorD,uv.y);
    color2 = mix(colorE,color,uv.y);

    color2 = mix(colorA,color,sin(iTime - uv.y)) + mix(colorA,color,sin(iTime + uv.y)) ;
       // Output to screen
    color = mix(color,color2,1.);
    //fragColor = vec4(color2 + color,1.0);
    fragColor = vec4(color,1.);
}
