void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = (2.0 * fragCoord - iResolution.xy)/iResolution.y;
  	vec3 color;
	float d;	

	float deg = 3.14 * iTime;

	float x = cos(deg) * 0.1;
	float y = sin(deg) * 0.1;	

	d = distance(vec2(cos(x) * 0.2, sin(y) * 0.2), vec2(.0)) + 0.09;

    float m = mod(iTime * 0.03, 2.0);	
    float p = (smoothstep(0.8, 1.2, m) - smoothstep(1.6, 2.0, m)) * 30.6 + 1.4;

	d *= length(min(abs(uv) * (abs(sin(iTime * 0.2))) * 0.3, 0.1) * p);    
	float mul = ((sin(iTime * 0.8)) * 5.0);
	float col = smoothstep(0.0, 0.7, fract(d * mul)) - smoothstep(0.7, 1.0, fract(d * mul));

	d /= length(max(abs(uv) - 0.0001, 0.225));    
	mul = 10.0;    
	col /= smoothstep(0.0, 0.7, fract(d * mul)) - smoothstep(0.7, 1.0, fract(d * mul));   

    fragColor = vec4(vec3(col), 1.0);											
}
