void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 fw =  fwidth(fragCoord.xy);

    float freq = 10.0;
    float onesqare = 1.0/freq;
    float selectedRow = mod(iTime*2.0,9.0);
	vec4 color = vec4(0.0,0.0,0.0,1.0);
	vec2 fuzz = fw*freq;fragCoord;
	float fuzzMax = max(fuzz.s,fuzz.t);

    vec2 res = fragCoord.xy/iResolution.xy;
	vec2 p = fract((res)*freq);

    vec2 current = res;

	float circleRadius_ =0.3;
	vec2 circleCenter = vec2(0.5,0.5);
	float dist = length(p - circleCenter );
		float circle = smoothstep(circleRadius_, circleRadius_ - 0.15, dist);


	color = vec4(circle,circle,circle,1);
    if(circle != 0.0 && current.x > selectedRow*onesqare  && current.x < (selectedRow+1.0)*onesqare) {

        color = vec4(1,0,0,1);

    }



     fragColor = color;   

}
