const int steps = 4;

float bayer8(ivec2 uv)
{   
    uv %= 8;
	return texelFetch(iChannel0,uv,0).r;
}

float ramp(float t)
{
    t=smoothstep(0.,1.,t);
    t=smoothstep(0.,1.,t);
    return t;
}

float sdf(vec2 p, float time)
{    
    const float tau = acos(-1.)*2.;
    const float duration = 5.;
    const float revolutions = 40.;
    float t = ramp(mod(time,duration)/duration)*revolutions;
    float angle = t*tau;
    return length(p-vec2(sin(angle),cos(angle)))-.2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x*=iResolution.x/iResolution.y;
    uv*=4.;

    float edge = dFdx(uv.x)*.5;

    float c = 0.;
    for (int i = 0; i < steps; ++i) {
        float subsample = bayer8(ivec2(fragCoord));
        if (iMouse.z > 0.) {
			subsample = .5;
        }
        float time = iTime + ((float(i)+subsample)/float(steps)-.5) * iTimeDelta;
        c += smoothstep(-edge,edge,sdf(uv,time));
    }
    c/=float(steps);

    c = pow(c,.4545);

    fragColor = vec4(vec3(c),1.0);
}
