void mainImage( out vec4 col, in vec2 coord )
{
    // BLUR
    vec4 t_c = texture(iChannel0, coord/iResolution.xy) / 2.;
    vec4 t_tr = texture(iChannel0, (coord + vec2(cos(iTime) * 10.))/iResolution.xy) / 4.;
    vec4 t_dl = texture(iChannel0, (coord - vec2(sin(iTime) * 10.))/iResolution.xy) / 4.;

    col = t_c + t_tr + t_dl;

    // DEFAULT
    //col = texture(iChannel0, coord/iResolution.xy);
}
