void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0, 0.0, 0.0);

    color = mix(color, vec3(0.0, 1.0, 1.0), step(0.0, st.x));
    color = mix(color, vec3(1.0, 1.0, 0.0), step(0.340, st.x));
    color = mix(color, vec3(1.0, 0.0, 1.0), step(0.674, st.x));

    float pi = 3.14;
    float c = 1.5;
    float v1 = abs(sin(iTime * (pi + 0.1) * c));
    float v2 = abs(sin(iTime * (pi + -0.756) * c));
    float v3 = abs(sin(iTime * (pi + 0.816) * c));

    color = color + vec3(1.0, 0.0, 0.0) * step(0.560 * v1, st.y);
    color = color + vec3(0.0, 1.0, 0.0) * step(0.880 * v2, st.y);
    color = color + vec3(0.0, 0.0, 1.0) * step(0.592 * v3, st.y);

    fragColor = vec4(color, 1.0);
}
