float odd(in float number){

	return step(1.0,mod(number,2.0));
}

vec2 zoomAndMove(in vec2 st,in float scale){

    st *= scale; 
    st.x += max(0.0,odd(st.y)*sin(iTime));
    st.y += min(0.0, odd(st.x)*sin(iTime));
	return fract(st);
}


float circle(in vec2 st, in vec2 origin, in float radius){

	float dist = length(st-origin);
    return step(radius, dist);
}






void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv =uv*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec2 st = zoomAndMove(uv,4.0);

    vec3 col = vec3(0.0);
    col = vec3(circle(st, vec2(0.5), 0.15));



    // Output to screen
    fragColor = vec4(col,1.0);
}
