void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec3 color;
 float iTime = iTime * 1.;
 vec2 uv = fragCoord.xy / iResolution.xy;;
 vec4 fout;

 float uvy = 0.25;
    // row 1
 if( uv.x < 0.25 && uv.y < uvy )
 {

  color = vec3(abs(sin(iTime-0.75)), 0., 1.);
  fout = vec4(color,1.);   
 }

 else if(  uv.x > 0.25 && uv.x < 0.5 && uv.y < uvy)
 {
  color = vec3(abs(sin(iTime-0.5)), 0., 1.);
  fout = vec4(color,1.0);   
 } 

 else if(  uv.x > 0.5 && uv.x < .75 && uv.y < uvy)
 {

  color = vec3(abs(sin(iTime-0.25)), 0., 1.);
  fout = vec4(color,1.0);
 }

 else if(  uv.x > 0.75 && uv.x < 1. && uv.y < uvy)
 {
  color = vec3(abs(sin(iTime)), 0, 1.);
  fout = vec4(color,1.0);
 }


    // row 2
float olduvy = uvy;   
uvy+= 0.25;    

 if( uv.x < 0.25 && uv.y < uvy && uv.y> olduvy  )
 {

  color = vec3(abs(sin(iTime+0.75)), 0., .1);
  fout = vec4(color,1.0);   
 }

 else if(  uv.x > 0.25 && uv.x < 0.5 && uv.y < uvy && uv.y> olduvy)
 {
  color = vec3(abs(sin(iTime+0.5)), 0., .2);
  fout = vec4(color,1.0);   
 } 

 else if(  uv.x > 0.5 && uv.x < .75 && uv.y < uvy && uv.y> olduvy)
 {

  color = vec3(abs(sin(iTime+0.25)), 0., .3);
  fout = vec4(color,1.0);
 }

 else if(  uv.x > 0.75 && uv.x < 1. && uv.y < uvy && uv.y> olduvy)
 {
  color = vec3(abs(sin(iTime+0.)), 0, .4);
  fout = vec4(color,1.0);
 }   

 //  row 3
  olduvy = uvy;   
uvy+= 0.25;    

 if( uv.x < 0.25 && uv.y < uvy && uv.y> olduvy  )
 {

  color = vec3(abs(cos(iTime-0.)), 0., 0.);
  fout = vec4(color,1.0);   
 }

 else if(  uv.x > 0.25 && uv.x < 0.5 && uv.y < uvy && uv.y> olduvy)
 {
  color = vec3(abs(sin(iTime-0.25)), 0., 0.);
  fout = vec4(color,1.0);   
 } 

 else if(  uv.x > 0.5 && uv.x < .75 && uv.y < uvy && uv.y> olduvy)
 {

  color = vec3(abs(sin(iTime-0.25)), 0., 0.);
  fout = vec4(color,1.0);
 }

 else if(  uv.x > 0.75 && uv.x < 1. && uv.y < uvy && uv.y> olduvy)
 {
  color = vec3(abs(cos(iTime)), 0, 0.);
  fout = vec4(color,1.0);
 }   


    //row 4
  olduvy = uvy;   
  uvy+= 0.25;    

 if( uv.x < 0.25 && uv.y < uvy && uv.y> olduvy  )
 {

  color = vec3(abs(sin(iTime-0.)), 0.5, 0.);
  fout = vec4(color,1.0);   
 }

 else if(  uv.x > 0.25 && uv.x < 0.5 && uv.y < uvy && uv.y> olduvy)
 {
  color = vec3(abs(cos(iTime-0.25)), 0.5, 0.);
  fout = vec4(color,1.0);   
 } 

 else if(  uv.x > 0.5 && uv.x < .75 && uv.y < uvy && uv.y> olduvy)
 {

  color = vec3(abs(sin(iTime-0.5)), 0.5, 0.);
  fout = vec4(color,1.0);
 }

 else if(  uv.x > 0.75 && uv.x < 1. && uv.y < uvy && uv.y> olduvy)
 {
  color = vec3(abs(cos(iTime-0.75)), 0.5, 0.);
  fout = vec4(color,1.0);
 }   

 fragColor = vec4(fout);
}
