void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 l_color = texture(iChannel1, uv);
    vec4 f_color = texture(iChannel0, uv);
    f_color.rgb = f_color.rgb * l_color.a;
    fragColor = f_color;
}

#define PI 3.14159;

float circle(vec2 pos) {
  float r = length(pos);
  r = sqrt(r * 1.);
  return abs(r);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;

    if(mouse.x == 0. && mouse.y == 0.){
        mouse.x = 0.27;
        mouse.y = 0.6;
    }

    uv -= vec2(mouse.x, mouse.y);
    vec2 ratioUv = uv;
    ratioUv.x = ratioUv.x * iResolution.x/iResolution.y * 0.75;
    float color2 = .35/circle(ratioUv);
    fragColor = vec4(color2);
}
