//mute channel1 and 2. Or don't. Try things. Go crazy. Be you, you squishy carbon based lifeform you.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float speed = 100.0;
    float zoom = 0.1 * (1.0 - mod(iTime, speed)/speed);
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ouv = uv;

    float rotation = mod(iTime/40.0, 100.0);

    ouv = vec2((uv.x - 0.5) * (iResolution.x / iResolution.y), uv.y - 0.5);
    ouv *= (2.0 - zoom) / 2.0;
    ouv *= mat2(cos(rotation), sin(rotation), -sin(rotation), cos(rotation));

    uv -= 0.56 * mod(iTime, speed);
    uv /= mod(iTime, speed);

    float xshifted = (uv.x - 0.5) * 512.0;

    float colorshift = texelFetch(iChannel0, ivec2(uv.x,0), 0).x;

    float startx = colorshift * zoom + mod(iTime, 10.0)/100.0;

    //vec2 z = vec2(startx - 0.01* mod(iTime,speed)/speed, +startx + 0.01* mod(iTime,speed));
    vec2 z = vec2(startx, -startx);

    float count = 0.0;

    for(int i = 0; i < 512; i++)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + uv;
        if(dot(z,z) > 1024.0){break;}
        count += 1.0;
    }

    float countF = float(count);

    float color = countF / 512.0;
    color = 1.0 - color;

    vec3 texColor = texelFetch(iChannel1,ivec2(ouv.x, ouv.y), 0).rgb;
    vec4 tex = texture(iChannel1,vec2(fract(ouv.x+(iTime/5.0)), fract(ouv.y+(iTime/12.0))));
    vec4 tex2 = texture(iChannel2,vec2(fract(ouv.x+(iTime/5.0)), fract(ouv.y+(iTime/12.0))));

    fragColor = vec4(vec3(colorshift* sin(iTime), color/2.0, 1.0 - colorshift/2.0) + texColor, (1.0));
    tex.r += sin(iTime);
    tex2.b += sin(iTime);
    tex2.g -= 0.48;

    vec3 mixed = mix(tex.rgb,tex2.rgb,color);

    fragColor = vec4(mixed,1.0);

}
