void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(texture(iChannel0, fragCoord/iResolution.xy).z);
}

precision lowp float;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
// current value
float v = texture(iChannel0,fragCoord/iResolution.xy).x;

// central diamond seed
if(abs(abs(fragCoord.x/iResolution.x-.55)
      +abs((1.2+fragCoord.x/iResolution.x)*fragCoord.y/iResolution.y*fragCoord.y/iResolution.y-.45)
      -.41)<.02)v = 1.;

// rule30 and variants
if( v < .5 ){
float a = texture(iChannel0,(fragCoord + vec2(2.,2.))/iResolution.xy).z +
    1.15 * texture(iChannel0,(fragCoord + vec2(0.,2.))/iResolution.xy).z +
    (1.2-.5*fragCoord.y/iResolution.y) * texture(iChannel0,(fragCoord + vec2(-2.,2.))/iResolution.xy).z;
v = (a > .5 ? 1. : 0. ) * (a < 3. ? 1. : 0.) ;
v = clamp(.45+3.*a*(1.-a),.0,1.);    
}

fragColor = vec4(v,v,v,1.);
}
