const int steps = 40;
const float rSteps = 1.0 / float(steps);

const int rays = 1;
const float rRays = 1.0 / float(rays);

const vec2 circlePos = vec2(0.5, 0.5);

float distField(vec2 p){
	float dist = length(p - circlePos) - 0.05;
	      dist = min(length(p - vec2(0.5, 0.63)) - 0.05, dist);
	      dist = min(length(p - vec2(0.5, 0.37)) - 0.05, dist);

	return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = fragCoord.xy / iResolution.xy;
	     position = position *2.0 - 1.0;
	     position.y *= iResolution.y / iResolution.x;
	     position = position * 0.5 + 0.5;

    vec2 mouse = iMouse.xy / iResolution.xy;

	vec3 color = vec3(0.0);
	vec2 p = position;
	vec2 dir = normalize(vec2(1.0, mouse.y * 2.0- 1.0));
	vec2 originalDir = dir; 

	float t = 0.0;
	vec2 originPoint = vec2(0.2, 0.5);
    vec2 newPoint = originPoint;

	bool hit = false;
	float d = 0.0;

	color = distField(position) < 0.001 ? vec3(0.5) : color;

    for (int i = 0; i < steps; ++i){
        float m = distField(newPoint);
        d += m;
        if (m < 0.001 || d > 2.0) {color = mix(color, vec3(0.0, 1.0, 0.0), step(distance(position, newPoint), 0.005)); break;}

        float distpos = distance(position, newPoint);

        color = max(color, 
			    max(step(abs(distpos - m), 0.001) * vec3(1.0, 0.0, 0.0),
			    step(distpos, 0.005)* vec3(0.0, 0.0, 1.0)));

        newPoint += dir * m;
    }


	fragColor = vec4(color, 1.0 );
}
