vec4 nyan(vec2 uv, float i) {
    uv.x-=0.14;
    i=mod(floor(i*15.), 6.);
    float j = 40./256.;
    uv.x*=j*1.4;
    if(uv.x>0.01 && uv.x<0.15){return texture(iChannel0, uv+vec2(j,0)*i);}
    else{return vec4(1.,1.,1.,0.);}
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 front = nyan(uv,iTime);
    //vec4 front = texture(iChannel0,uv);

    vec3 h_color_top = vec3(0.,0.21,0.25);
	//vec3 h_color_top = vec3(0.27,0.08,0.2);
	vec3 h_color_bottom = vec3(1.,0.95,0.55);
	float h_brt = 0.3 * front.r + 0.59 * front.g + 0.11 * front.b;
	h_brt*=1.2;
	h_brt-=0.05;
	vec4 h_grad_clr = vec4(h_color_bottom * (h_brt*2.), 1) + vec4(h_color_top * (1.19 - (h_brt)), 1);

    front.b=mod(clamp(front.b+0.7,0.,0.6)+0.05,1.);

    fragColor = mix(front*h_grad_clr+0.07,vec4(h_color_top,1.),0.4*front.a);
}
