#define USE_TONE 0

vec3 Tone(vec3 col)
{
    return pow( clamp( col, 0.0, 1.0 ), vec3(0.25345f, 0.45f, 0.45f) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(0.0);

    if( iFrame > 0 )
    {
        col = texture( iChannel0, uv ).xyz;
        col = pow( col, vec3(0.4545) );
    }

     fragColor = mix( vec4(col, 1.0), vec4(Tone(col), 1.0), float(USE_TONE));
}

#define USE_WARP 0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // warp/reset FX
    float rate = mix( 1.0, tan(iTime * 2.0), float(USE_WARP));

    // lower == more
    // recommend 30 or greater
    float blinds = 50.0;

    vec3 colour;
    colour.r = mod(fragCoord.x + float(iFrame) + rate, blinds) / 255.0;
    colour.g = mod(fragCoord.x + -float(iFrame) + rate, blinds) / 255.0;
    colour.b = 1.0;

    fragColor = vec4(colour, 1.0);
}
