float Circle(vec2 uv, vec2 pos, float r, float blur) {
    return smoothstep(r, r - blur, length(uv - pos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;

    float c = Circle(uv, vec2(.0, .0), .4, .01);

    for (float i = 0., n = -1.; i < 35.; i++, n = -n) {
        c += n * Circle(uv, vec2(.0, .0), .01 * i, .001 * i);
    }


    fragColor = c * vec4(1, 1, 0, 1);
}
