// variant of https://shadertoy.com/view/tslBWf

void mainImage(out vec4 O, vec2 U) {

    float T = iTime, t=1.,a;
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),        // ray direction
          p = 30./q;                              // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p.xzy - vec3(0,0,T),
        t = abs(length(q.xy)-6.)-2.,              // bounding hollow cylinder
        a = atan(q.y,q.x) - .3*T,
        t = max(t, sin(a-floor(q.z*4.)/8. )),     // helix: stairs side
        t = max(t,-sin(a-floor(q.z*4.+.3)/8.)+.04 ),  // helix: roof side
        p += .8*t*D;                              // step forward = dist to obj
}
