// Fork of "Voronoi Triangulation" by wyatt. https://shadertoy.com/view/WtcSz7
// 2020-05-07 21:16:34

Main {
    Q = D(U)*(1.-max(0.,sin(iTime))*exp(-2.*length(U-B(U).xy)));
}

#define R iResolution.xy
#define A(U) texture(iChannel0,floor(U)/R)
#define B(U) texture(iChannel1,floor(U)/R)
#define C(U) texture(iChannel2,floor(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage (out vec4 Q, vec2 U)
#define Init if (iFrame < 1) 
#define Border if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.)
float sg (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	if (length(a-b)<1e-3) return 1e3; // ignore self
	float l = (length(p-a-(b-a)*i));
    if (l < 1.) return length(a-b);
    return l;
}
float mp (vec2 p, vec2 a, vec2 b) {
	vec2 m = 0.5*(a+b); // midpoint
    if (length(a-b)<1e-3) return 1e3; // ignore self
	return dot(p-m,b-m)/dot(b-m,b-m); // pojection
}
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float tri (vec2 u, vec2 a, vec2 b, vec2 c) {
	float ab = sg(u,a,b);
	float bc = sg(u,b,c);
    float ca = sg(u,c,a);
    return min(ab,min(bc,ca));
}

// Nearest Individual
void X (inout vec4 Q, vec2 U, vec2 r) {
    vec4 n = B(U+r);
    float ln = length(n.xy-U), la = length(Q.xy-U),
         l = abs(sg(U,Q.xy,Q.zw)),
         o = abs(sg(U,Q.xy,n.zw)),
         o1 = abs(sg(U,Q.xy,n.xy));
    if (ln<la) {
    	Q.xy = n.xy;
    }
    if (o<l) {
    	Q.zw = n.zw;
    }
    if (o1<l) {
    	Q.zw = n.xy;
    }
}
void Xr (inout vec4 Q, vec2 U, float r) {
	 X(Q,U,vec2(r,0));
     X(Q,U,vec2(0,r));
     X(Q,U,vec2(0,-r));
     X(Q,U,vec2(-r,0));
}
vec2 grad (vec2 u) {
	vec4
        n = D(u+vec2(0,1)),
        e = D(u+vec2(1,0)),
        s = D(u-vec2(0,1)),
        w = D(u-vec2(1,0));
    return vec2(e.w-w.w,n.w-s.w);
}
Main {
	Q = B(U);
    Xr(Q,U,1.);
    Xr(Q,U,2.);
    Xr(Q,U,3.);
    Xr(Q,U,4.);
    float err = D(Q.xy).w;
    Q.xy -= grad(Q.xy)*err;
    Q.zw = B(Q.zw).xy;
    vec2 f = vec2(0);
    for (int x = -2; x<=2;x++) {
        for (int y = -2; y<=2;y++) {
        	 vec4 b1 = B(Q.xy+vec2(x,y));
        	vec2 r = b1.zw-Q.xy;
            float l = length(r);
            if (l>0.) 	f += .001*r/l/l/(1e-3+err);
        }
    }
    Q.xy += f;
    if (iFrame%6==int((U.x*+R.x*U.y))%6&&D(U).w>.2) Q.xy = U;
    Init {
    	U = floor(U/100.+0.5)*100.;
        Q = vec4(U,0,0);
    }
}

Main {
    Q = vec4(0);
    vec4 b = B(U);
    Q += 2.*A(b.xy)/(.1+dot(U-b.xy,U-b.xy));
    for (int x = -3; x<=3;x++) {
        for (int y = -3; y<=3;y++) {
        	 vec4 b1 = B(b.xy+3.*vec2(x,y));
            Q += A(b1.zw)/(.1+2.*dot(U-b1.zw,U-b1.zw));
            b1 = B(b.zw+4.*vec2(x,y));
            Q += A(b1.xy)/(.1+2.*dot(U-b1.zw,U-b1.zw));
        }
    }
    Q /= 1e-4+Q.w;
    // Error
    vec3 a = A(U).xyz;
    Q.w = 10.*dot(a-Q.xyz,a-Q.xyz);
    vec4 m = 0.25*(D(U+vec2(0,1))+D(U+vec2(1,0))+D(U-vec2(0,1))+D(U-vec2(1,0)));
    Q.w = mix(m.w,Q.w,0.1);
}
